const bubbleIcon = "data:image/svg+xml;base64,";
const blockIcon = "data:image/svg+xml;base64,";
  
class Color {
  getInfo() {
    return {
      id: 'lxColorManip',
      name: 'Colors',
      color1: '#ABBF2C',
      menuIconURI: bubbleIcon,
      blockIconURI: blockIcon,
      blocks: [
        {
          opcode: 'colorColor',
          blockType: Scratch.BlockType.REPORTER,
          text: 'color [COL]',
          arguments: {
            COL: {
              type: Scratch.ArgumentType.COLOR
            }
          }
        },
        {
          opcode: 'rgbOfColor',
          blockType: Scratch.BlockType.REPORTER,
          text: '[RGB] of color [COL]',
          arguments: {
            COL: {
              type: Scratch.ArgumentType.COLOR
            },
            RGB: {
              type: Scratch.ArgumentType.STRING,
              menu: 'RGB_MENU'
            }
          }
        }
      ],
      menus: {
          RGB_MENU: {
          acceptReporters: true,
          items: [{text: 'Red', value: 'R'}, {text: 'Green', value: 'G'}, {text: 'Blue', value: 'B'}]
        }
      }
    };
  }

  colorColor(args) {
    return args.COL;
  }
  rgbOfColor(args) {
    if (args.RGB === 'R') {
      return args.COL.charAt(1) + args.COL.charAt(2);
    } else if (args.RGB === 'G') {
      return args.COL.charAt(3) + args.COL.charAt(4);
    } else {
      return args.COL.charAt(5) + args.COL.charAt(6);
    }
  }
}
Scratch.extensions.register(new Color());
